import React, { useEffect, useState } from "react";
import {
  Box,
  Button,
  Checkbox,
  FormControl,
  FormLabel,
  Grid,
  Heading,
  Image,
  Input,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  Stack,
  Text,
  useDisclosure,
  useToast,
} from "@chakra-ui/react";
import { useDispatch, useSelector } from "react-redux";
import { AddIcon } from "@chakra-ui/icons";
import { addOrderItems } from "../Redux/Order/order.Action";
import { useNavigate } from "react-router-dom";
import { cartRest } from "../Redux/Cart/cart.Action";

const initialAddress = {
  city: "",
  state: "",
  mobile: "",
  Pincode: "",
};

const CheckOut = () => {
  const [address, setAddress] = useState(initialAddress);
  const [paymentMethod, setPaymentMethod] = useState("cash");
  const [total, setTotal] = useState(0);
  const toast = useToast();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { cartData } = useSelector((store) => store.cart);

  useEffect(() => {
    setTotal(
      cartData
        .reduce((acc, el) => acc + Number(el.price * el.qty), 0)
        .toFixed(2)
    );
  }, [cartData]);

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setAddress({ ...address, [name]: value });
  };

  const handlePaymentMethodChange = (e) => {
    setPaymentMethod(e.target.value);
  };

  const handleBuyNow = () => {
    if (
      address.user === "" ||
      address.address === "" ||
      address.phone === "" ||
      address.note === ""
    ) {
      return toast({
        title: "Please fill Address.",
        description: `Address is required for delivery`,
        status: "error",
        duration: 2000,
        isClosable: true,
      });
    }
    dispatch(addOrderItems(cartData));
    toast({
      title: "Order Success.",
      description: `Order Amount VND:- ${total}`,
      status: "success",
      duration: 2000,
      isClosable: true,
    });
    navigate("/order");
    dispatch(cartRest());
  };

  const totalItems = cartData.length;
  // const totalPrice = cartData.reduce((acc, item) => acc + item.price, 0);

  function InitialFocus() {
    const { isOpen, onOpen, onClose } = useDisclosure();
    const initialRef = React.useRef(null);
    const finalRef = React.useRef(null);

    return (
      <>
        <Button onClick={onOpen}>thêm địa chỉ mới</Button>
        <Modal
          initialFocusRef={initialRef}
          finalFocusRef={finalRef}
          isOpen={isOpen}
          onClose={onClose}
        >
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>địa chỉ của bạn</ModalHeader>
            <ModalCloseButton />
            <ModalBody pb={6}>
              <FormControl mt={4}>
                <FormLabel>Tên người nhận</FormLabel>
                <Input
                  type="text"
                  name="user"
                  value={address.user}
                  onChange={handleAddressChange}
                  placeholder="user"
                />
              </FormControl>
              <FormControl mt={4}>
                <FormLabel>note</FormLabel>
                <Input
                  type="text"
                  name="note"
                  value={address.stanotete}
                  onChange={handleAddressChange}
                  placeholder="note"
                />
              </FormControl>
              <FormControl mt={4}>
                <FormLabel>Địa chỉ nhận hàng</FormLabel>
                <Input
                  type="number | string"
                  name="address"
                  value={address.address}
                  onChange={handleAddressChange}
                  placeholder="number"
                />
              </FormControl>
              <FormControl mt={4}>
                <FormLabel>Số điện thoại</FormLabel>
                <Input
                  type="number"
                  name="phone"
                  value={address.phone}
                  onChange={handleAddressChange}
                  placeholder="phone"
                />
              </FormControl>
            </ModalBody>

            <ModalFooter>
              <Button onClick={onClose} colorScheme="blue" mr={3}>
                Save
              </Button>
              <Button onClick={onClose}>Cancel</Button>
            </ModalFooter>
          </ModalContent>
        </Modal>
      </>
    );
  }

  return (
    <Box p={4} mt={"50px"} background={"#9F3194"} color={"white"}  >
      <Heading color={"black"} textAlign={"center"} >
        Checkout
      </Heading>

      <Stack spacing={4} >
        <Heading as="h2" size="md" mb={2}>
          Delivery Address
        </Heading>
        <Box color={"black"}>
          <AddIcon /> {InitialFocus()}
        </Box>
        <Grid
          templateColumns="repeat(3, 1fr)"
          alignItems=""
          background="black"
          color="white"
           display={"flex"}
          gap={"10px"}
          justifyContent={"space-between"}
          mb={2}
        >
          <Text fontWeight="bold" >
            City:- <Box color={"black"}> <input type="text" defaultValue={address.city} /></Box>
          </Text>
          <Text fontWeight="bold" >
            State:- <Box color={"black"}> <input type="text" defaultValue={address.state} /></Box>
          </Text>
          <Text fontWeight="bold" >
            Mobile No.:- <Box color={"black"}> <input type="text" defaultValue={address.mobile} /></Box>
          </Text>
          <Text fontWeight="bold" >
            PinCode:- <Box color={"black"}> <input type="text" defaultValue={address.Pincode} /></Box>
          </Text>
        </Grid>

        <Heading as="h2" size="md" color={"white"} mb={2}>
          Cart Items ({totalItems})
        </Heading>
        <Box height={"200px"} overflowY={"scroll"} background={"black"} color={"white"} id="example">
          {cartData.map((item) => (
            <Grid
              key={item._id}
              templateColumns="repeat(4, 1fr)"
              alignItems="center"
              gap={2}
              mb={2}
              border={"1px solid white"}
            >
              <Image
                objectFit="cover"
                height="200px"
                width={"auto"}
                src={item.image}
                alt={item.title}
                padding="10px"
              />
              <Text fontSize={"bold"}>{item.title}</Text>
              <Text fontSize={"bold"}>{item.qty}</Text>
              <Text>${item.price}</Text>
            </Grid>
          ))}
        </Box>

        <Box>
          <Grid templateColumns="repeat(2, 1fr)" gap={4} alignItems="center" background={"black"} color={"white"}>
            <Text>Total Items:</Text>
            <Text>{totalItems}</Text>
            <Text>Total Quantity:</Text>
            <Text>{cartData.reduce((acc, el) => acc + Number(el.qty), 0)}</Text>
            <Text>Total Price:</Text>
            <Text>${total}</Text>
          </Grid>
        </Box>

        <FormControl background={"black"} color={"white"}>
          <FormLabel>Payment Method</FormLabel>
          <Stack direction="row" spacing={4}>
            <Checkbox
              value="cash"
              isChecked={paymentMethod === "cash"}
              onChange={handlePaymentMethodChange}
            >
              Cash on Delivery
            </Checkbox>
            <Checkbox
              value="card"
              isChecked={paymentMethod === "card"}
              onChange={handlePaymentMethodChange}
            >
              Card
            </Checkbox>
          </Stack>
        </FormControl>

        <Button colorScheme="teal" onClick={handleBuyNow}>
          Buy Now
        </Button>
      </Stack>
    </Box>
  );
};

export default CheckOut;
